import React from "react";
import { StyledArticlesPage } from "./Styles/StyledArticlesPage";
import ArticlesList from "./ArticlesList";
import FilterBy from "./FilterBy";
import SortBy from './SortBy'

class ArticlesPage extends React.Component {
  state = {};

  handleState = (event) => {
    this.setState(event.target.innerText)
  }

  render() {
    return (
      <StyledArticlesPage>
        <h1>Articles</h1>

        <div className="Toolbar">
          <FilterBy />
          <div className="dropdown">
            <button className="dropbtn">Login</button>
            <div className="dropdown-content">
              <button onClick={this.handleState}>grumpy19</button>
              <button href="#/">jessjelly</button>
              <button href="#/">tickle122</button>
              <button href="#/">happyamy2016</button>
              <button href="#/">cooljmessy</button>
              <button href="#/">weegembump</button>
            </div>
          </div>
        <SortBy/></div>
        <ArticlesList topic={this.props.topic} />
      </StyledArticlesPage>
    );
  }
}

export default ArticlesPage;
