import styled from "styled-components";

export const StyledHeader = styled.nav`
transition: top 0.3s;
position : fixed;
width: 100%;
  ul {
    list-style-type: none;
    margin: 0;
    padding: 0 15px;
    overflow: hidden;
    background-color: #333;
  }
  li {
    display: block;
    color: white;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
    float: left;
    font-family : "Courier new"
  }
  .right {
    float: right;
  }
`;
