import styled from "styled-components";

export const StyledFullArticle = styled.div`
    width : 
  border: 2px solid black;
  display: flex;
  flex-direction: column;
  justify-content: center;
  font-family: "Courier new";
  .top {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    padding: 5px 20px;
    .topic {
      font-style: italic;
    }
    .author:hover {
       cursor: pointer;
    }
  }
  .bot {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    padding: 5px 20px;
  }
  .content {
    padding: 15px 20px;
  }
`;
