import styled from "styled-components";

export const StyledHeader = styled.div`
transition: top 0.3s;
position : fixed;
top: 0;
width: 100%;
overflow : hidden;
z-index: 10;


  ul {
    list-style-type: none;
    margin: 0;
    padding: 0px 15px;
    overflow: hidden;
    background-color: rgb(166, 3, 66);
