import styled from 'styled-components'

export const StyledArticlesPage = styled.div`
padding : 120px 0px 0px 0px;
font-family : 'Courier new';
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;

z-index: 10;
.Toolbar {
    padding-top : 2%;
    width : 100%;
    display:flex;
    flex-direction:row;
    justify-content :space-evenly;

}
`
