import React from "react";
import { StyledArticlesPage } from "./Styles/StyledArticlesPage";
import ArticlesList from "./ArticlesList";
import FilterBy from "./FilterBy";
import SortBy from "./SortBy";
import Login from "./Login";

class ArticlesPage extends React.Component {
  state = {
    userSelected: "",
    sortBy: null,
    orderBy : null
  };

  changeStateUser = value => {
    this.setState({ userSelected: value });
    this.props.changeUser(this.state.userSelected);
  };

  changeStateSorting = (sortValue, orderValue) => {
    this.setState({sortBy : sortValue, orderBy: orderValue})
  }


  render() {
    const { user, topic } = this.props;
    const { sortBy, orderBy } = this.state;
    return (
      <StyledArticlesPage>
        <h1>Articles</h1>

        <div className="Toolbar">
          <FilterBy />
          {user !== "" ? (
            <p>Logged in as {user}</p>
          ) : (
            <Login changeState={this.changeStateUser} />
          )}
          <SortBy sortState={this.changeState}/>
        </div>
        <ArticlesList topic={topic} sortBy={sortBy} orderBy={orderBy}/>
      </StyledArticlesPage>
    );
  }
}

export default ArticlesPage;
