import React from "react";
import * as api from "../api";
import { StyledCommentBox } from "./Styles/StyledCommentBox";
import CommentCard from "./CommentCard";

class CommentBox extends React.Component {
  state = {
    commentList: [],
    username: "weegembump",
    commentValue: "Type your comment here."
  };
  componentDidMount() {
    this.getComments();
  }
  componentDidUpdate(prevProps, prevState) {
      if (this.state.commentList !== prevState.commentList)
          this.getComments()
  }

  getComments = () => {
    api.getCommentsByID(this.props.id).then(comments => {
      this.setState({ commentList: comments });
    });
  };
  handleSubmit = event => {
    event.preventDefault();
    api
      .postComment(this.props.id, this.state.username, this.state.commentValue)
      .then(comment => {
        this.setState(prev => {
          return { commentList: [...prev.commentList, comment] };
        });
      });
  };
  handleChange = event => {
    this.setState({ commentValue: event.target.value });
  };

  render() {
    return (
      <StyledCommentBox>
        {this.state.commentList.map(comment => (
          <CommentCard key={comment.comment_id} comment={comment} />
        ))}
        <form className="form" onSubmit={this.handleSubmit}>
          <textarea
            value={this.state.value}
            onChange={this.handleChange}
            className="comment"
          ></textarea>

          <button className="submitButt" type="submit">
            Submit
          </button>
        </form>
      </StyledCommentBox>
    );
  }
}

export default CommentBox;
