import styled from 'styled-components'

export const StyledCommentCard = styled.ul`
font-weight : normal;
list-style-type : none;
align-self : flex-start;
li{
    display: flex;
    flex-direction: row;
    text-align: justify; 
    padding-top : 10px;
}
.author:hover {
      cursor: pointer;
      color : black;

    }
    a {
      text-decoration: none;
      color : rgb(166, 3, 66);
    }
.fa {
    padding-right : 5px;
}
`
