import React from "react";
import { StyledSortBy } from './Styles/StyledSortBy'


class SortBy extends React.Component {
  state = {
    sorted: "",
    ordered : ""
  };

  changeStateAsc = (event) => {
    console.log(event.target.innerText)
    this.setState({ sorted : event.target.innerText, ordered : "asc"
    })
  }
  render() {
      return (
        <StyledSortBy>
      <div className="dropdown">
        <button
          className="btn btn-primary dropdown-toggle"
          type="button"
          data-toggle="dropdown"
        >
          Sort By
          <span className="caret"></span>
        </button>
        <ul className="dropdown-menu">
          <li>
            <button onClick={this.changeStateAsc}>Date - Asc</button>
          </li>
          <li>
            <button >Comment - Asc</button>
          </li>
          <li>
            <button>Votes- Asc</button>
          </li>
        </ul>
      </div></StyledSortBy>
    );
  }
}

export default SortBy;
