  state = {
    article: {}
  };

    componentDidMount
  getArticleById = () => {
    api.getArticleById(this.props.id).then(article => console.log(article));
  };
  render() {
    console.log(this.props.id);
    return (
      <StyledSingleArticlePage>
        <FullArticle />
