import styled from "styled-components";

export const StyledFilterBy = styled.ul`
margin : 0;
padding : 2px;

    list-style-type: none;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
    border : 1px solid grey;

`;
