import styled from "styled-components";

export const StyledFilterBy = styled.ul`
  margin: 0;
  padding : 0;
  text-align : justify;
  list-style-type: none;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  border: 1px solid grey;
  border-radius : 10px;

  button {
      align-self : center;
      border : none;
      padding : 5px;
      font-size : 15px;
      border-radius : 10px;
  }
  li {

     margin-top : 5px;
      padding : 3px;
      border-top : 1px solid grey;
  }
`;
