import React from "react";
import { StyledSortBy } from './Styles/StyledSortBy'


class SortBy extends React.Component {
  state = {
    sorted: "",
    ordered : ""
  };

  changeStateAsc = (event) => {
    console.log(event.target.value)
    this.setState({ sorted : event.target.value, ordered : "asc"
    })
    this.props.sortState(this.state.sorted, this.state.ordered)
  }
  changeStateDesc = (event) => {
    console.log(event.target.value)
    this.setState({ sorted : event.target.value, ordered : "desc"
    })
    this.props.sortState(this.state.sorted, this.state.ordered)
  }
  render() {
      return (
        <StyledSortBy>
      <div className="dropdown">
        <button
          className="btn btn-primary dropdown-toggle"
          type="button"
          data-toggle="dropdown"
        >
          Sort By
          <span className="caret"></span>
        </button>
        <ul className="dropdown-menu">
          <li>
            <button value = "created_at" onClick={this.changeStateAsc}>Date - Asc</button>
          </li>
          <li>
            <button value = "comment_count" onClick={this.changeStateAsc}>Comment - Asc</button>
          </li>
          <li>
            <button value = "votes" onClick={this.changeStateAsc}>Votes- Asc</button>
              </li>
              <li>
            <button value = "created_at" onClick={this.changeStateAsc}>Date - Descsc</button>
          </li>
          <li>
            <button value = "comment_count" onClick={this.changeStateAsc}>Comment - Asc</button>
          </li>
          <li>
            <button value = "votes" onClick={this.changeStateAsc}>Votes- Asc</button>
          </li>
        </ul>
      </div></StyledSortBy>
    );
  }
}

export default SortBy;
