import styled from "styled-components";

export const StyledHeader = styled.nav`
transition: top 0.3s;
position : relative;
top: 0;
width: 100%;
overflow : hidden;
z-index: 0;

  ul {
    list-style-type: none;
    margin: 0;
    padding: 0 15px;
    overflow: hidden;
    background-color: rgb(166, 3, 66);
    max-height : 100px;
  }
  li {
    display: block;
    color: white;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
    float: left;
    font-family : "Courier new";
    font-size : 25px;
  }
  .right {
    float: right;
  }
  a{
    color : white;
    text-decoration: none;
  }
  .dropdown {
    display : flex;
    flex-direction: column;
    justify-content : center;
    position : relative;

  }
  .dropdown-content{
    display : flex; 
    position: absolute;
    flex-direction: column;
    z-index : 10;
    background-color : red;
  }
  button {
    border : none;
    background-color : rgb(166, 3, 66);
  }


`;
