import React from "react";
import * as api from '../api'

class FilterBy extends React.Component {
  state = {
    button: false,
    topics: []
  };
    componentDidMount() {
        this.getTopics()
    }

    getTopics = () => {
        api.getTopics().then((topics) => {
            this.setState({topics : topics})
        })
    }

  showOptions = () => {
    this.setState(prev => {
      return { button: !prev.button };
    });
  };

  render() {
    return (
      <ul>
        <button onClick={this.showOptions}>FilterBy</button>
        {this.state.button && <li>topics</li>}
      </ul>
    );
  }
}

export default FilterBy;
