import styled from "styled-components";

export const StyledFilterBy = styled.div`
  .filter{
 display: block;
 transition-duration: 0.5s;
}

.filter:hover {
  cursor: pointer;
}

.dropdown  {
  visibility: hidden;
  opacity: 0;
  position: absolute;
  transition: all 0.5s ease;
  margin-top: 1rem;
  left: 0;
  display: none;
}

ul li:hover > ul,
ul li ul:hover {
  visibility: visible;
  opacity: 1;
  display: block;
}

ul li ul li {
  clear: both;
  width: 100%;
}

`;
