import styled from "styled-components";

export const StyledCommentBox = styled.div`
  margin-top: 60px;
  padding: 30px;
  color: black;
  border-top: 1px solid grey;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: flex-start;
`;
