import React from "react";
import { StyledSortBy } from "./Styles/StyledSortBy";

class SortBy extends React.Component {


  changeStateAsc = event => {
    const {value}= event.target
    this.setState({ sorted: value, ordered: "asc" })
    this.props.sortState(value, "asc");
  };
  changeStateDesc = event => {
    console.log("sort by here");
    this.setState({ sorted: event.target.value, ordered: "desc" });
    this.props.sortState(this.state.sorted, this.state.ordered);
  };
  render() {
    console.log("state", this.state.sorted)
    return (
      <StyledSortBy>
        <div className="dropdown">
          <button
            className="btn btn-primary dropdown-toggle"
            type="button"
            data-toggle="dropdown"
          >
            Sort By
            <span className="caret"></span>
          </button>
          <ul className="dropdown-menu">
            <li>
              <button value="created_at" onClick={this.changeStateAsc}>
                Date - Asc
              </button>
            </li>
            <li>
              <button value="created_at" onClick={this.changeStateDesc}>
                Date - Desc
              </button>
            </li>
            <li>
              <button value="comment_count" onClick={this.changeStateAsc}>
                Comment - Asc
              </button>
            </li>
            <li>
              <button value="comment_count" onClick={this.changeStateDesc}>
                Comment - Desc
              </button>
            </li>
            <li>
              <button value="votes" onClick={this.changeStateAsc}>
                Votes- Asc
              </button>
            </li>

            <li>
              <button value="votes" onClick={this.changeStateDesc}>
                Votes- Desc
              </button>
            </li>
          </ul>
        </div>
      </StyledSortBy>
    );
  }
}

export default SortBy;
