import React from 'react';

class ViewToggler extends React.Component {
    state = { 
        showContent: false
    }
    changeState = () => {
        this.setState(prev => {
            return { showContent: !prev.showContent }
        })
    }
    render() { 
        return ( 
            <button onClick = {this.changeState}> {this.state.showContent ? <p>Hide Comments</p> : <p>ShowComments</p>}</button>
         );
    }
}

export default ViewToggler;
