import styled from "styled-components";

export const StyledSortBy = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  max-width:20%;

  button {
    font-size: 15px;
    font-weight: bolder;
    background-color: rgb(166, 3, 66);
  }
  .dropdown-menu{
      min-width : 10px;
      max-width: 10px;
  }
`;
