import styled from 'styled-components'

export const StyledCommentCard = styled.ul`
font-weight : normal;
list-style-type : none;
align-self : flex-start;
li{
    display: flex;
    flex-direction: row;
    text-align: justify; 
    padding-top : 10px;
}
.fa {
    padding-right : 10px;
}
`
