import React from "react";
import * as api from "../api";
import { StyledCommentBox } from "./Styles/StyledCommentBox";
import CommentCard from "./CommentCard";

class CommentBox extends React.Component {
  state = {
    commentList: [],
    username: this.props.user,
    commentValue: "Type your comment here.",
    deleteId: 0
  };
  componentDidMount() {
    this.getComments();
  }
  componentDidUpdate(prevProps, prevState) {
    if (this.state.deleteId !== this.state.deleteId)
      this.deleteComm();
  }

  getComments = () => {
    api.getCommentsByID(this.props.id).then(comments => {
      this.setState({ commentList: comments });
    });
  };
  deleteComm = () => {
    api.deleteComment(this.state.deleteId).then(() => {
      this.setState({deleteId : 0})
    })
  }
  requestDelete = (commentId) => {
    console.log(commentId)
    this.setState({deleteId : commentId})
  }

  handleSubmit = event => {
    event.preventDefault();
    api
      .postComment(this.props.id, this.state.username, this.state.commentValue)
      .then(comment => {
        this.setState(prev => {
          return { commentList: [comment, ...prev.commentList], commentValue: "Type your comment here." };
        });
      });
  };
  handleChange = event => {
    this.setState({ commentValue: event.target.value });
  };

  render() {
    console.log(this.state.deleteId)
    return (
        <StyledCommentBox>
            <form className="form" onSubmit={this.handleSubmit}>
          <textarea
            value={this.state.commentValue}
            onChange={this.handleChange}
            className="comment"
          ></textarea>

          <button className="submitButt" type="submit">
            Submit
          </button>
        </form>
        {this.state.commentList.map(comment => (
          <CommentCard key={comment.comment_id} comment={comment} requestDelete={this.requestDelete}/>
        ))}

      </StyledCommentBox>
    );
  }
}

export default CommentBox;
