import styled from "styled-components";

export const StyledSingleArticlePage = styled.div`
  margin-top: 60px;
  display: flex;
  justify-content: center;
  font-family: "Courier new";
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  p {
    font-weight: bolder;
    font-size: 15px;
  }
`;
