import React from "react";
import FullArticle from "./FullArticle";
import * as api from "../api";
import { StyledSingleArticlePage } from "./Styles/StyledSingleArticlePage";

class SingleArticlePage extends React.Component {
  state = {
    article: {}
  };

  componentDidMount() {
    this.getArticleById();
  }

  getArticleById = () => {
      api.getArticleById(this.props.id).then(article => this.setState({ article: article, isLoading: false }));
  };

  render() {
    console.log(this.props.id);
    return (
      <StyledSingleArticlePage>
        <FullArticle />
      </StyledSingleArticlePage>
    );
  }
}

export default SingleArticlePage;
