import axios from "axios";

export const getArticles = () => {
  return axios
    .get("https://nc-news-api-marie.herokuapp.com/api/articles")
    .then(({ data }) => {
      return data.articles;
    });
};

export const getArticleById = () => {
  return axios.get 
}
