import styled from "styled-components";

export const StyledFullArticle = styled.div`
  width: 70%;
  border: 2px solid black;
  border-radius : 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  font-family: "Courier new";
  .top {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    padding: 5px 20px;
    color : rgb(166, 3, 66);
    font-weight : bolder;
    .topic {
      font-style: italic;
    }
    .author:hover {
      cursor: pointer;
      font-size : 15px;

    }
  }
  .bot {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    padding: 5px 20px;
    color :rgb(166, 3, 66);
    font-weight : bolder;
  }
  .content {
    padding: 15px 20px;
  }
`;
