import React from "react";

class FilterBy extends Component {
  state = { button: false };

  showOptions = () => {
    this.setState(prev => {
      return { button: !prev.button };
    });
  };

  render() {
    return (
      <ul>
        <button onClick={this.showOptions}>FilterBy</button>
        {this.state.button && <li>topics</li>}
      </ul>
    );
  }
}

export default FilterBy;
