import React from "react";
import FullArticle from "./FullArticle";
import * as api from "../api";
import { StyledSingleArticlePage } from "./Styles/StyledSingleArticlePage";
import Login from "./Login";

class SingleArticlePage extends React.Component {
  state = {
    article: {},
    isLoading: true,
    err : null
  };

  componentDidMount() {
    this.getArticleById();
  }

  getArticleById = () => {
    api
      .getArticleById(this.props.id)
      .then(article => this.setState({ article: article, isLoading: false })).catch(err => {
        console.log(err)
        this.setState({ isLoading: false, err : {msg : err.msg, status : err.status} });

      })
  };

  render() {
  console.log(this.state.err)
  if (this.state.err) return <h1>{this.state.err.msg}</h1>
    return (
      <StyledSingleArticlePage>
        {this.props.user !== "" ? (
          <p className="LoggedIn">Logged in as {this.props.user}</p>
        ) : (
          <Login changeState={this.props.changeUser} />
        )}
        {this.state.isLoading ? (
          <h2>Loading...</h2>
        ) : (
          <FullArticle article={this.state.article} user={this.props.user} />
        )}
      </StyledSingleArticlePage>
    );
  }
}

export default SingleArticlePage;
