import React from "react";
import CommentsToggler from "./CommentsToggler";
import { StyledFullArticle } from "./Styles/StyledFullArticle";
import { Link } from "@reach/router";
import * as api from '../api'

class FullArticle extends React.Component {
  state = {
    votes: 0,
    difference: 0
  };

  changeVotes = value => {
    this.setState(prev => {
      return { votes: prev.votes + value };
    });
    api.updateVotes(this.props.article.article_id, value ).then()
  };
  render() {
    const { article } = this.props;

    return (
      <StyledFullArticle>
        <div className="top">
          <Link to={`/users/${article.author}`}>
            <p className="author">
              <i className="fa fa-user-circle"></i> {article.author}
            </p>
          </Link>
          <p className="topic">{article.topic}</p>
        </div>

        <div className="content">
          <h2>{article.title}</h2>
          <p>{article.body}</p>
        </div>

        <div className="bot">
          <p className="votes">
            Votes : {this.state.votes}{" "}
            <button onClick={event => this.changeVotes(1)}>Upvote</button>
            <button onClick={event => this.changeVotes(-1)}>Downvote</button>
          </p>

          <p className="created_at">{article.created_at}</p>
        </div>
        <CommentsToggler className="commentToggler" id={article.article_id} />
      </StyledFullArticle>
    );
  }
}

export default FullArticle;
